import React, { Component } from 'react';
import axios from 'axios';
import QueryTab from './QueryTab';

class Admin extends Component {


    render(arr) {
        return (
            <div id="Admin">
                <div  id="AdminNavbar"> 
                    <h1  id="AdminHeading">Admin Panel</h1>
                </div>
                <div id="TabBar">
                    <div className="Tab" id="QueriesTab">
                        User Queries
                    </div>
                    <div className="Tab" id="GalleryTab">
                        Image Gallery
                    </div>
                    <div className="Tab" id="GalleryTab">
                        Add User
                    </div>
                    <div className="Tab" id="GalleryTab">
                        Change Password
                    </div>
                </div>
                <div id="TabWindows">
                    <div id="QueriesList">
                        {this.state.queries}
                     </div>
                    <div id="QueryBtn">
                        <div><button id="NextBtn" onClick={this.NextPage}>Next Page</button></div>
                        <div><button id="PrevBtn" onClick={this.PrevPage}>Prev Page</button></div>
                        <div id="PageNo">Page : {(no/10)+1}</div>
                        <div><button id="DelPageBtn" onClick={this.DeletePage}>Delete Page</button></div>                     
                    </div>
                </div>        
            </div>
        );
    }
}

export default Admin;
