import React, { Component } from 'react';
import './Styles/Slider.css'
import slider1 from './Images/slider1.jpg'
import slider2 from './Images/slider1.jpg'
import slider3 from './Images/slider1.jpg'
import slider4 from './Images/slider1.jpg'
import slider5 from './Images/slider1.jpg'
var index=0;
const sliderimages = [slider1,slider2,slider3,slider4,slider5];
console.log(sliderimages);
console.log(curr);


class Slider extends Component {
    constructor(){
        super();
        this.state.curr= sliderimages[0];
    }

    Nextimg() {
        if(index===5)
        {
            curr=sliderimages[0];
            index=0;
        }
        else
        {
            index=index+1;
            curr=sliderimages[index]
        }    
    }

    render() {
        return (
            <div id='slider'>
                <img src={this.state.curr}></img>
            </div>
        );    
    }

    componentDidMount(){
        this.timerID = setInterval(
            () => this.Nextimg(),
            1000
        );    
    }
}


export default Slider;
