import React, { Component } from 'react';
import axios from 'axios';
import './Styles/Form.css';

class Form extends Component {
    constructor(){
        super();
        this.state = {name:"" , phone:0 , email:"" , query:""}
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = event => {
        this.setState({
          [event.target.name]: event.target.value
        });
    }

    handleSubmit(event) {
        axios
        .post("https://localhost:9000/query", this.state)
        .then(res => console.log(res))
        .catch(err => console.log(err));
      }

    render() {
        return (
            <div id="Form">
                <div id="FormHeading">
                    <h1>Query Form</h1>
                </div>
                <div id="FormText">
                    Have any Questions?  Feel free to communicate.  
                    We will get back to you within 24 hours.
                </div>
                <form id="FormContainer">
                    <div className="FormElements">
                        <div className="FormLabel">Name :</div>
                        <div className="FormInput"><input name="name" type='text' onChange={this.handleChange} /></div>
                    </div>
                    <div className="FormElements">
                        <div className="FormLabel">Phone :</div>
                        <div className="FormInput"><input name="phone" type='tel' onChange={this.handleChange}/></div>
                    </div>
                    <div className="FormElements">
                        <div className="FormLabel">Email :</div>
                        <div className="FormInput"><input name="email" type='email' onChange={this.handleChange}/></div>
                    </div>
                    <div className="FormElements">
                        <div className="FormLabel">Query :</div>
                        <div className="FormInput"><textarea name="query" onChange={this.handleChange}/></div>
                    </div>
                    <input type='submit' onClick={this.handleSubmit}/>
                </form>
            </div>
        );
    }
}

export default Form;
