import React, { Component } from 'react';
import axios from 'axios';
import './Styles/Admin.css';

class Admin extends Component {
    constructor()
    {
        super();
        this.state = {};
    }

    componentDidMount(){

        axios.get("http://localhost:9000/admin")
        .then(res => {
            const queries = res.data.map( u =>
                <div key={u._id} className='query'>
                <p>{u.name}</p>
                <p>{u.phone}</p>
                <p>{u.email}</p>
                <p>{u.query}</p>
                </div>
                );
            this.setState({queries});
        })
        .catch(err => console.log(err));
    }

    render(arr) {
        return (
            <div id="Admin">
                <div  id="AdminNavbar"> 
                    <h1  id="AdminHeading">Admin Panel</h1>
                </div>
                <div id="TabBar">
                    <div class="Tab" id="QueriesTab">User Queries</div>
                </div>
                <div id="TabWindows">
                    <div id="QueriesList">
                        {this.state.queries}
                    </div>
                </div>        
            </div>
        );
    }
}

export default Admin;
