import React, { Component } from 'react';
import './Styles/Slider.css'
import slider1 from './Images/slider1.jpg'
import slider2 from './Images/slider2.jpg'
import slider3 from './Images/slider3.jpg'
import slider4 from './Images/slider4.jpg'
import slider5 from './Images/slider5.jpg'
import Heading from './Heading';

class Slider extends Component {
    constructor(){
        super();
        this.sliderimages = [slider1, slider2, slider3, slider4, slider5];
        this.slidertext = ['Maaxo Industries', 'Modern Technologies',
                            'ISO Certified','Quality products', ' Long lasting']
        this.state={index:0};
    }

    Nextimg = () => {
        if(this.state.index===4)
        {
            this.setState({index:0});
        }
        else
        {
            this.setState({index:this.state.index + 1});
        }   
    }

    render() {
        return (
            <div>
                <div id='slider'>
                    <img src={this.sliderimages[this.state.index]}></img>
                    <div id="Heading"><Heading /></div>
                    <h2>{this.slidertext[this.state.index]}</h2>   
                    <div id='sliderarrows'>
                        <p id='prev'>prev</p>
                        <p id='next'>next</p>
                    </div>
                </div>    
            </div> 
        ); 
    }

    componentDidMount(){
        this.timerID = setInterval(
            () => this.Nextimg(),
            3000
        );    
    }
}


export default Slider;
