import React, { Component } from 'react';
import "./Styles/Login.css";

class Login extends Component {
    constructor(){
        super();
        this.state = {username:"" , password:""};
    }

    handleChange = event => {
        this.setState({
          [event.target.name]: event.target.value
        });
    }


    handleSubmit = event => {
        event.preventDefault();
        const data={
            name:this.state.name,
            email:this.state.email,
            phone:this.state.phone,
            query:this.state.query
        };
        var config = {
            headers: {'Access-Control-Allow-Origin': '*',
                    'Access-Control-Allow-Credentials': true}
        }; 
        axios
        .post("http://localhost:9000/add", data, config)
        .then(function(res){alert(res.data)})
        .catch(err => console.log(err))
        this.setState({name:"" , phone:"" , email:"" , query:""});

    }

    render() {
        return (
            <div id="Login">
                <div id="LoginForm">
                    <div className="LoginLabel">Username</div>
                    <div className="LoginInput"><input onChange={this.handleChange} name="username" id="username" type="text"></input></div>
                    <div className="LoginLabel">Password</div>
                    <div className="LoginInput"><input onChange={this.handleChange} name="password" id="password" type="password"></input></div>
                    <div className="LoginButton"><input type="submit" onClick={this.handleSubmit}></input></div>
                </div>
            </div>
        );
    }

}

export default Login;
