import React, { Component } from 'react';
import axios from 'axios';
import { Redirect } from "react-router-dom";
import "./Styles/Login.css";

class Login extends Component {
    constructor(){
        super();
        this.state = {username:"" , password:""};
    }

    handleChange = event => {
        this.setState({
          [event.target.name]: event.target.value
        });
    }


    handleSubmit = event => {
        event.preventDefault();
        document.getElementById("Message").innerHTML = "";    
        var config = {
            headers: {'Access-Control-Allow-Origin': '*',
                    'Access-Control-Allow-Credentials': true}
        }; 
        axios
        .post("http://localhost:9000/login", this.state, config)
        .then(function(res)
            {
                if(res.data === 'y'){
                    console.log("SUCCESS");
                    return  <Redirect  to="/admin/" />

                }
                else if(res.data === 'n'){
                console.log("Failure");
                return  <Redirect  to="/home/" />      } 
            })
        .catch(err => console.log(err))
        this.setState({username:"", password:""});
        document.getElementById("username").value = "";
        document.getElementById("password").value = ""; 
    }


    render() {
        return (
            <div id="Login">
                <div id="LoginForm">
                    <div className="LoginLabel">Username</div>
                    <div className="LoginInput"><input onChange={this.handleChange} name="username" id="username" type="text"></input></div>
                    <div className="LoginLabel">Password</div>
                    <div className="LoginInput"><input onChange={this.handleChange} name="password" id="password" type="password"></input></div>
                    <div className="LoginButton"><input type="submit" onClick={this.handleSubmit} value="Login"></input></div>
                </div>
                <div id="Message"></div>
            </div>
        );
    }

}

export default Login;
