import React, { Component } from 'react';
import axios from 'axios'

class Login extends Component {
    constructor(){
        super();
        this.state = {username:"" , password:""};
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const data={
            name:this.state.name,
            email:this.state.email,
            phone:this.state.phone,
            query:this.state.query
        };

        var config = {
            headers: {'Access-Control-Allow-Origin': '*',
                    'Access-Control-Allow-Credentials': true}
        };

        axios
        .post("http://localhost:9000/add", data, config)
        .then(function(res){alert(res.data)})
        .catch(err => console.log(err))

        this.setState({username :"" , password:""});
    }

    handleChange = event => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    render() {
        return (
            <div>
                <div id="LoginFormHeading">
                        <h1>Login Form</h1>
                </div>
                <form id="loginFormContainer">
                    <div className="LoginFormElements">
                        <div className="LoginFormLabel">Username :</div>
                        <div className="LoginFormInput"><input name="username" type='text' value={this.state.username} onChange={this.handleChange} /></div>
                    </div>
                    <div className="LoginFormElements">
                        <div className="LoginFormLabel">Password :</div>
                        <div className="LoginFormInput"><input name="password" type='password' value={this.state.password} onChange={this.handleChange}/></div>
                    </div>
                    <input type='submit' onClick={this.handleSubmit}/>
                </form>    
            </div>
        );
    }
}

export default Login;
