import React, { Component } from 'react';
import './Styles/Slider.css'
import slider1 from './Images/slider1.jpg'
import slider2 from './Images/slider1.jpg'
import slider3 from './Images/slider1.jpg'
import slider4 from './Images/slider1.jpg'
import slider5 from './Images/slider1.jpg'


class Slider extends Component {
    constructor(){
        super();
        this.sliderimages = [slider1,slider2,slider3,slider4,slider5];
        this.state={index:0};
    }

    Nextimg = () => {
        if(this.state.index===4)
        {
            this.setState({index:0});
        }
        else
        {
            this.setState({index:this.state.index + 1});
        }  
        console.log(this.sliderimages) 
    }

    render() {
        return (
            <div id='slider'>
                <img src={this.sliderimages[this.state.index]}></img>
            </div>
        );    
    }

    componentDidMount(){
        this.timerID = setInterval(
            () => this.Nextimg(),
            1000
        );    
    }
}


export default Slider;
