import React, { Component } from 'react';
import axios from 'axios';
import "./Styles/Login.css";

class Login extends Component {
    constructor(){
        super();
        this.state = {username:"" , password:""};
    }

    handleChange = event => {
        this.setState({
          [event.target.name]: event.target.value
        });
    }


    handleSubmit = event => {
        event.preventDefault();
        console.log(this.state)
        var config = {
            headers: {'Access-Control-Allow-Origin': '*',
                    'Access-Control-Allow-Credentials': true}
        }; 
        axios
        .post("http://localhost:9000/login", this.state, config)
        .then(function(res){alert(res.data)})
        .catch(err => console.log(err))
        this.setState({username:"", password:""});
    }

    render() {
        return (
            <div id="Login">
                <div id="LoginForm">
                    <div className="LoginLabel">Username</div>
                    <div className="LoginInput"><input onChange={this.handleChange} name="username" id="username" type="text" value=""></input></div>
                    <div className="LoginLabel">Password</div>
                    <div className="LoginInput"><input onChange={this.handleChange} name="password" id="password" type="password" value=""></input></div>
                    <div className="LoginButton"><input type="submit" onClick={this.handleSubmit} value="Login"></input></div>
                </div>
                <div id="message"></div>
            </div>
        );
    }

}

export default Login;
