import React, { Component } from 'react';

class Login extends Component {
    constructor(){
        super();
        this.state = {username:"" , password:""};
    }

    handleChange(event){
        setState({[event.target.name]: event.target.value});
    }

    render() {
        return (
            <div>
                <div id="LoginFormHeading">
                        <h1>Login Form</h1>
                </div>
                <form id="loginFormContainer">
                    <div className="FormElements">
                        <div className="FormLabel">Username :</div>
                        <div className="FormInput"><input name="name" type='text' value={this.state.username} onChange={this.handleChange} /></div>
                    </div>
                    <div className="FormElements">
                        <div className="FormLabel">Password :</div>
                        <div className="FormInput"><input name="phone" type='password' value={this.state.password} onChange={this.handleChange}/></div>
                    </div>
                </form>    
            </div>
        );
    }
}

export default Login;
