import React, { Component } from 'react';
import './Styles/Slider.css'
import slider1 from './Images/slider1.jpg'
import slider2 from './Images/slider2.jpg'
import slider3 from './Images/slider3.jpg'
import slider4 from './Images/slider4.jpg'
import slider5 from './Images/slider5.jpg'
import Heading from './Heading';
import pdf from '../brochure.pdf'
var clicked = 0;

class Slider extends Component {
    constructor(){
        super();
        this.sliderimages = [slider1, slider2, slider3, slider4, slider5];
        this.slidertext = ['Maaxo Industries', 'Modern Technologies',
                            'ISO Certified','Quality products', ' Long lasting']
        this.explanation = ['Long service history','Leak proof and strong products',
                            'ISO 2001:2005 certified', 'Quality is never compromised',
                            'Guaranteed to last over generations']
        this.state={index:0};

    }

    Nextimg = () => {
        if(this.state.index===4)
        {
            this.setState({index:0});
        }
        else
        {
            this.setState({index:this.state.index + 1});
        }
        clearInterval(this.timerID);
        this.componentDidMount();  
    }

    Previmg = () => {
        if(this.state.index===0)
        {
            this.setState({index:4});
        }
        else
        {
            this.setState({index:this.state.index - 1});
        }
        clearInterval(this.timerID);
        this.componentDidMount();    
    }

    sizeofsliderid = () => {
        const slider=document.getElementById("slider");
        slider.style.height = window.innerHeight + 'px';
    }

    DotClick = () => {
        this.setState({index:0});
        clearInterval(this.timerID);
        this.componentDidMount();
    }

    render() {
        return (
            <div>
                <div id='slider' >
                    <img src={this.sliderimages[this.state.index]}></img>
                    <div id="Heading">
                        <Heading />
                        <a id='pdf' href={pdf} target="_blank"><button>Download Brochure</button></a>
                    </div>   
                    <div id='sliderarrows'>
                        <h3 id='prev' onClick={this.Previmg}>prev</h3>
                        <h3 id='sliderdesc'><span onClick={this.DotClick}>a</span>
                                            <span onClick={this.DotClick}>a</span>
                                            <span onClick={this.DotClick}>a</span>
                                            <span onClick={this.DotClick}>a</span>
                                            <span onClick={this.DotClick}>a</span>
                        </h3>
                        <h3 id='next' onClick={this.Nextimg}>next</h3>
                    </div>
                    <h3 id="explanation">{this.explanation[this.state.index]}</h3>
                </div>   
            </div> 
        ); 
    }
    componentDidMount(){
        this.sizeofsliderid();
        this.timerID = setInterval(
            () => this.Nextimg(),
            4500
        );    
    }
}


export default Slider;
