import React, { Component } from 'react';
import './Styles/Heading.css'

class Heading extends Component {
    constructor() {
        super();
        this.changingText=['Serving People since 1995','Excellence Guaranteed',
                    'wonderful','soulful','modern'];
        this.state = {index:0};
    }

    changeText = () => {
        if(this.state.index === 4)
        this.setState({index : 0});
        else
            this.setState({index : this.state.index+1});
    }

    render() {
        return (
            <div id="Heading">
                <div class="HeadingDiv"><h1 id="HeadingText">MAAXO</h1></div>
                <div id="ChangingText"><em>{this.changingText[this.state.index]}</em></div>
            </div>
        );
    }

    componentDidMount() {
        setInterval(this.changeText,6000);
    }
}

export default Heading;
