import React, { Component } from 'react';
import axios from 'axios';
import './Styles/Admin.css';
var no=0;
var len = 0;
class Admin extends Component {
    constructor()
    {
        super();
        this.state = {};
    }

    componentDidMount(){
        const url = "http://localhost:9000/admin?no="+no; 
        axios.get(url)
        .then(res => {
            console.log(res.value)
            console.log(res.value.data);
            const queries = res.data.map( u =>
                <div key={u._id} className='query'>
                <p>Name  :{u.name}</p>
                <p>Phone :{u.phone}</p>
                <p>Email :{u.email}</p>
                <p>Query :{u.query}</p>
                </div>
                );
            this.setState({queries});
        })
        .catch(err => console.log(err));
    }

    NextPage = () =>{
            no = no+10;
            this.componentDidMount();  
    }
    PrevPage = () =>{
        no = no-10;
        this.componentDidMount();  
    }

    render(arr) {
        return (
            <div id="Admin">
                <div  id="AdminNavbar"> 
                    <h1  id="AdminHeading">Admin Panel</h1>
                </div>
                <div id="TabBar">
                    <div class="Tab" id="QueriesTab">
                        User Queries
                    </div>
                    <div class="Tab" id="GalleryTab">
                        Image Gallery
                    </div>
                    <div class="Tab" id="GalleryTab">
                        Add User
                    </div>
                    <div class="Tab" id="GalleryTab">
                        Change Password
                    </div>
                </div>
                <div id="TabWindows">
                    <div id="QueriesList">
                        {this.state.queries}
                    </div>
                    <div id="QueryBtn">
                        <div><button id="NextBtn" onClick={this.NextPage}>Next Page</button></div>
                        <div><button id="PrevBtn" onClick={this.PrevPage}>Prev Page</button></div>
                    </div>
                </div>        
            </div>
        );
    }
}

export default Admin;
