import React, { Component } from 'react';
import './Styles/Brochure.css';

class Brochure extends Component {
    noBorder = () => {
        var buttom = document.querySelectorAll("button");
        var i;
        for (i = 0; i < button.length; i++) {
            button[i].style.border = "none";
        } 
    }

    render() {
        return (
            <div id="Brochure">
                <div id="BrochureLink">
                    <div onClick={this.noBorder}><a href="../brochure.pdf" download><button>Download Brochure</button></a></div>
                </div> 
                <div id="ImagesLink">
                    <div><a onClick={this.noBorder}><button>More Images</button></a></div>
                </div>
            </div>
        );
    }
}

export default Brochure;
