import React, { Component } from 'react';
import axios from 'axios';

class Admin extends Component {
    constructor()
    {
        super();
        this.state = {};
    }

    componentDidMount(){

        axios.get("http://localhost:9000/admin")
        .then(res => {
            const data = res.data;
            console.log(data);
            const queries = data.map(u =>
                <div className='query'>
                <p>{u.id}</p>
                <p>{u.name}</p>
                <p>{u.email}</p>
                <p>{u.query}</p>
                </div>
                );
            this.setState({queries});
        })
        .catch(err => console.log(err));
    }

    render(arr) {
        return (
            <div>
                <h1>admin page</h1>
                {this.state.queries}
            </div>
        );
    }
}

export default Admin;
