import React, { Component } from 'react';
import './Styles/Floating.css' 
import Slider from './Slider'

class Floating extends Component {
    render() {
        return (
            <div className="Floating">
                <Slider />
            </div>
        );
    }

    componentDidMount()
    {
        var floating= document.querySelectorAll('.Floating')
        for( var i =0;i<floating.length; i++)
        {   floating[i].style.width = this.props.w;
            floating[i].style.height = this.props.h;
            floating[i].style.marginLeft = this.props.ml;
            floating[i].style.marginTop = this.props.mt;
        }
    }
}

export default Floating;
